import { UPDATE_USER } from './ActionTypes';

const initialState = {
  user: {}, // Initial empty user object
};

const userReducer = (state = initialState, action) => {
  switch (action.type) {
    case UPDATE_USER:
      return {
        ...state,
        user: action.payload,
      };
    default:
      return state;
  }
};

export default userReducer;
